command.body:

$cmd.sql("""
DECLARE
	scname varchar2(20);
BEGIN
	scname := '${schema.toUpperCase()}';

	--Bye Sequences!
	for i in (select us.sequence_name from all_sequences us where us.sequence_owner = scname) loop
		execute immediate 'drop sequence '||scname||'.'|| i.sequence_name;
	end loop;

	--Bye Tables!
	for i in (select ut.table_name from all_tables ut where ut.owner = scname) loop
		execute immediate 'drop table '||scname||'.'|| i.table_name ||' cascade constraints purge';
	end loop;

	--Bye Synonyms!
	for i in (select us.synonym_name from dba_synonyms us where us.owner = scname) loop
		execute immediate 'drop synonym '||scname||'.'|| i.synonym_name;
	end loop;
end;
""")


if(!$cmd.exec("checkDBUser", [schema:sourceSchema])) {
	$cmd.error("Invalid user '${sourceSchema}' to create dump file")
}

$cmd.exec("backupSchema", [schema:destSchema])

$cmd.exec("truncateDBUser", [schema:destSchema])

def osService = $cmd.findOf("SSH")

$cmd.ssh("su - oracle -c 'expdp ${target.username}/${target.password} schemas=${sourceSchema} directory=${target.prop.pumpDbDir} dumpfile=${sourceSchema}.dmp logfile=${sourceSchema}.log'", osService)

$cmd.ssh("su - oracle -c 'impdp ${target.username}/${target.password} remap_schema=${sourceSchema}:${destSchema} directory=${target.prop.pumpDbDir} dumpfile=${sourceSchema}.dmp logfile=${destSchema}.log'", osService, true)

$cmd.ssh("su - oracle -c 'rm -f ${target.prop.pumpOsDir}/${sourceSchema}.dmp'", osService)


if(!$cmd.exec("checkDBUser", [schema:schema])) {
	$cmd.error("Invalid user '${schema}' to create dump file")
}

def osService = $cmd.findOf("SSH")

$cmd.ssh("su - oracle -c 'expdp ${target.username}/${target.password} schemas=${schema} directory=${target.prop.pumpDbDir} dumpfile=${schema}_bkup_${$cmd.now()}.dmp logfile=${schema}.log'", osService)
